    function uploadecg($userid, $date, $symptoms, $pre_illness, $pdf, $images){
        $conn = dbConnect();
        $sql = "insert into qwert_ecgs(`userid`, `ecgid`, `rec_date`, `symptoms`, `pre_illness`, `pdf`, `images`, `assign_dt`, `status`, `create_date`) values('".$userid."',NULL,'".$date."','".$symptoms."','".$pre_illness."','".$pdf."','".$images."','1','Pending For payment', CURRENT_TIMESTAMP())";
